var menuOpen=!1;document.addEventListener("DOMContentLoaded",n=>{const t=document.getElementById("navbar"),e=Array.from(t.children);if(e[0].offsetTop!=e[e.length-1].offsetTop){const s=document.createElement("link");s.rel="stylesheet",s.href="/mobile.css",document.head.appendChild(s);const n=document.createElement("div");n.classList.add("navitem"),n.innerHTML="&lt;Menu&gt;",n.style.fontStyle="italic",e.forEach(e=>e.style.display="none"),document.getElementById("navbar").appendChild(n),n.addEventListener("mouseup",s=>{menuOpen?(menuOpen=!1,e.forEach(e=>e.style.display="none"),n.innerHTML="&lt;Menu&gt;",t.style.backgroundColor="transparent"):(menuOpen=!0,e.forEach(e=>e.style.display="inline-block"),n.innerHTML="&lt;Close Menu&gt;",t.style.backgroundColor="#000000aa")})}})