var menuOpen=!1;document.addEventListener("DOMContentLoaded",e=>{const n=document.getElementById("navbar"),t=Array.from(n.children);if(t[0].offsetTop!=t[t.length-1].offsetTop){const s=document.createElement("link");s.rel="stylesheet",s.href="/mobile.css",document.head.appendChild(s);const e=document.createElement("div");e.classList.add("navitem"),e.innerHTML="&lt;Menu&gt;",e.style.fontStyle="italic",t.forEach(e=>e.style.display="none"),document.getElementById("navbar").appendChild(e),e.addEventListener("mouseup",s=>{menuOpen?(menuOpen=!1,t.forEach(e=>e.style.display="none"),e.innerHTML="&lt;Menu&gt;",n.style.backgroundColor="transparent"):(menuOpen=!0,t.forEach(e=>e.style.display="inline-block"),e.innerHTML="&lt;Close Menu&gt;",n.style.backgroundColor="#000000aa")})}})